import React, { Component } from 'react'

export class AddCard extends Component {
    state={
        content:'',
        price:0
    }
    handleContent=(e)=>{
        this.setState({
            content:e.target.value
        })
    }
    handlePrice=(e)=>{
        this.setState({
            price:e.target.value
        })
    }
    handleContent=(e)=>{
        this.setState({
            content:e.target.value
        })
    }
    handleIPrice=(e)=>{
        this.setState({
            price:e.target.value
        })
    }
    handleSubmit=(e)=>{
        e.preventDefault();
        this.props.addCard(this.state);
        this.setState({
            content:"",
            price:0
        })
    }

    render() {
        return (
            <div>
                <form onSubmit={this.handleSubmit}>
                    <label>Add new expenditure</label>
                    <input type="text" onChange={this.handleContent} value={this.state.content} ></input>
                    <label>Enter amount</label>
                    <input type="text" onChange={this.handlePrice} value={this.state.price}></input>
                    <button><h6>Submit</h6></button>
                </form>
                <form onSubmit={this.handleSubmit}>
                    <label>Add new earning</label>
                    <input type="text" onChange={this.handleIContent} value={this.state.content} ></input>
                    <label>Enter amount</label>
                    <input type="text" onChange={this.handleIPrice} value={this.state.price}></input>
                    <button><h6>Submit</h6></button>
                </form>
            </div>
        )
    }
}

export default AddCard
