import React, { Component } from 'react'
import Main from "./components/Main"
import "./App.css"
export class App extends Component {
  state={
    expense:0,
    earning:0,
    tot_earn:0,
    tot_spend:0
  }
  handleExpense=(e)=>{
    const prevearning=this.state.tot_earn;
    this.setState({
      expense:e.target.value,
      tot_earn:prevearning+e.target.value
    })
  }
  handleEarning=(e)=>{
    const prevspent=this.state.tot_spend;
    this.setState({
      earning:e.target.value,
      tot_spend:prevspent+this.state.tot_spend
    })
  }
  // handleSubmit=(e)=>{
  //   //e.preventDefault();
  //   this.setState({
  //     expense:(this.state.expense),
  //     earning:(this.state.earning)
  //   })
  // }
  render() {
    return (
      <div><center>
        <h1>Expense Tracker</h1>
        <Main handleEarning={this.handleEarning} handleExpense={this.handleExpense} earning={this.state.earning} expense={this.state.expense}/>
        </center></div>
    )
  }
}

export default App
