import React, { Component } from 'react'

export class AddCard extends Component {
    state={
        content:'',
        price:""
    }
    handleContent=(e)=>{
        this.setState({
            content:e.target.value
        })
    }
    handlePrice=(e)=>{
        this.setState({
            price:e.target.value
        })
    }
    handleSubmit=(e)=>{
        e.preventDefault();
        this.props.addCard(this.state);
        this.setState({
            content:"",
            price:""
        })
    }

    render() {
        return (
            <div>
                <form onSubmit={this.handleSubmit}>
                    <label>Add new expenditure</label>
                    <input type="text" onChange={this.handleContent} value={this.state.content} ></input>
                    <label>Enter amount</label>
                    <input type="text" onChange={this.handlePrice} value={this.state.price}></input>
                    <button><h6 style={{margin:"10px"}}>Submit</h6></button>
                </form>
            </div>
        )
    }
}

export default AddCard
