      {id:1,content:"Lays"},
      {id:2,content:"Lays1"},
      {id:3,content:"Lays2"},
    ]
  }
  //additem comes from AddCard.js on submition of a specific card with id
  addCard=(additem)=>{
    additem.id=Math.random();
    let items=[...content,additem]
    this.setState({
        content:[]
    })
}
  render() {
    return (
      <div className="todo-app container">

        <h1 className="center blue-text">Expense Tracker</h1> 
        <AddCard addCard={this.addCard}/>
        <Card items={this.state.items}/>

      </div>
    )
  }
}

export default App
